from rest_framework import serializersclass FilterDateSerializer(serializers.Serializer):    month = serializers.IntegerField()    year = serializers.IntegerField()    def validate_month(self, value):        if not 13 > value > 0:            raise serializers.ValidationError("Invalid month")        return valueclass ResponseEmployeesStatisticsSerializer(serializers.Serializer):    id = serializers.IntegerField()    fullname = serializers.CharField()    unique_clients = serializers.IntegerField()    clients = serializers.IntegerField()    products_count = serializers.IntegerField()    prices_sum = serializers.IntegerField()