from django.db import modelsclass ClientModel(models.Model):    full_name = models.CharField(max_length=255, db_index=True)    birthdate = models.DateField()    def __str__(self):        return f"{self.full_name}"    @property    def orders_count(self):        return self.orders.count()    @property    def products_count(self):        return self.orders.annotate(count_prod=models.Count("products")).aggregate(summa=models.Sum("count_prod")).get(            "summa") or 0    @property    def orders_price_sum(self):        return self.orders.aggregate(models.Sum("price")).get("price__sum") or 0    class Meta:        verbose_name_plural = "Clients"        verbose_name = "Client"        unique_together = ["full_name", "birthdate"]