from django.db import modelsfrom apps.shop.models import ClientModel, ProductModel, EmployeeModelclass OrderModel(models.Model):    client = models.ForeignKey(ClientModel, null=True, on_delete=models.SET_NULL, related_name="orders")    products = models.ManyToManyField(ProductModel, related_name="orders")    employee = models.ForeignKey(EmployeeModel, on_delete=models.SET_NULL, null=True, related_name="orders")    price = models.PositiveIntegerField()    date = models.DateField(auto_now_add=True)    def __str__(self):        return f"{self.client} {self.price}"