from django.db import modelsclass ProductModel(models.Model):    name = models.CharField(max_length=255)    quantity = models.PositiveIntegerField()    price = models.PositiveIntegerField()    def __str__(self):        return f"{self.name} {self.quantity} {self.price}"    class Meta:        verbose_name_plural = "Products"        verbose_name = "Product"